import React, { useEffect, useState } from "react";
import { useContextGlobal } from "./utils/global.context.jsx";
import dentistImg from "../assets/images/doctor.jpg"
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {
  const {dentists} = useContextGlobal();
  const [dentist, setDentists] = useState();
  const url = 'https://jsonplaceholder.typicode.com/users'

  useEffect(() =>{
    fetch(url)
    .then(response => response.json())
    .then(data => setDentists(data))
  }, [])

  const addFav = ()=>{
    let favs = JSON.parse(localStorage.getItem('favs')) || [];
    const favExistente = favs.find(fav => fav.id === dentist.id)
    if(!favExistente){
      const newFavs = [...favs, dentist];
      localStorage.setItem('favs', JSON.stringify(newFavs))
    }else{
      const newFavs = favs.filter((fav) => fav.id === dentist.id)
      localStorage.setItem('favs', JSON.stringify(newFavs))
    }
  }

  return (
    <>
    {dentists.map(dentist => (
      <div key = {dentist.id} className="card">
        <img src = {dentistImg} alt="dentist img" style={{ maxWidth: '100%' }} />
        <Link to = {`/detalle/${dentist.id}`}>
          <h4 >{dentist.name}</h4>
        </Link>
        <p>{dentist.username}</p>

        <Link to= "/favs" >
          <button onClick={() => addFav(dentist)} className="favButton">‚≠ê</button>
        </Link>
    </div>
    ))}
    </>

  );
};

export default Card;
