import React, { useEffect, useState } from "react";
import Card from "../Components/Card";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {
  const [favs, setFavs] = useState([]);

  useEffect(() => {
    const favsData = JSON.parse(localStorage.getItem("favs")) || [];
    setFavs(favsData);
  }, []);

  const handleToggleFav = (dentist) => {
    const favs = JSON.parse(localStorage.getItem("favs")) || [];

    const existingIndex = favs.findIndex((fav) => fav.id === dentist.id);
    if (existingIndex !== -1) {
      // Si el dentista ya existe en los favoritos, lo eliminamos
      const newFavs = [...favs.slice(0, existingIndex), ...favs.slice(existingIndex + 1)];
      localStorage.setItem("favs", JSON.stringify(newFavs));
      setFavs(newFavs);
    } else {
      // Si el dentista no existe en los favoritos, lo agregamos
      const newFavs = [...favs, dentist];
      localStorage.setItem("favs", JSON.stringify(newFavs));
      setFavs(newFavs);
    }
  };


  return (
    <>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
      {favs.map((fav) => (
          <Card key={fav.id} 
          dentistId={fav.id}
          onToggleFav={() => handleToggleFav(fav)}
          isFav
        />
        ))}
    </div>
    </>
  );
};

export default Favs;
