import React from "react";
import { useContextGlobal } from "./utils/global.context";
import dentistImg from "../assets/images/doctor.jpg"
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {
  const {dentists} = useContextGlobal();


  const addFav = (dentist)=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    const favorites = JSON.parse(localStorage.getItem("favs")) || {}; // Obtener los favoritos del localStorage o inicializar un objeto vacío
    favorites[dentist.id] = dentist; // Agregar la Card actual al objeto de favoritos usando el id como clave
    localStorage.setItem("favs", JSON.stringify(favorites)); 
  }

  return (
    <>
   <div key={id} className="card">
        <img
          src={dentistImg}
          alt="dentist img"
          style={{ maxWidth: "100%" }}
        />
        <Link to={`/detalle/${id}`}>
          <h4>{name}</h4>
        </Link>
        <p>{username}</p>

        <Link to="/favs">
          <button onClick={() => addFav({ id, name, username })} className="favButton">
            ⭐
          </button>
        </Link>
      </div>

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

    </>

  );
};

export default Card;
