import React, { useEffect, useState } from 'react'
import { Form, useParams } from 'react-router-dom'


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const {id} = useParams()
  const [selectedDentist, setSelectedDentist] = useState(null);
  const [formData, setFormData] = useState({});
  const [dentist, setDentist] = useState(null)
  const url = 'https://jsonplaceholder.typicode.com/users/' + id

  useEffect(() => {
    if (selectedDentist) {
      setFormData({
        name: selectedDentist.name,
        email: selectedDentist.email,
        phone: selectedDentist.phone,
        website: selectedDentist.website
      });
    }
  }, [selectedDentist]);

  if (!dentist) return null

  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  return (
    <>
      <h1>Detalle Dentista id {id}</h1>
    <p>Name: {selectedDentist?.name}</p>
    <p>Email: {selectedDentist?.email}</p>
    <p>Phone: {selectedDentist?.phone}</p>
    <p>Website: {selectedDentist?.website}</p>
    <Form data={formData} />
      {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </>
  )
}

export default Detail
