import React, { useState } from "react";
import { useContextGlobal } from "./utils/global.context.jsx";
import dentistImg from "../assets/images/doctor.jpg"
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {
  const {dentists} = useContextGlobal();
  const [dentist, setDentists] = useState();
  const favs = localStorage.getItem('favs') || []


  const addFav = ()=>{
    localStorage.setItem('dentist.name', JSON.stringify(dentist))
  }

  return (
    <>
    {dentists.map(dentist => (
      /* En cada card deberan mostrar en name - username y el id */
      <div key = {dentist.id} className="card">
        <img src = {dentistImg} alt="dentist img" style={{ maxWidth: '100%' }} />
        <Link to = {`/detalle/${dentist.id}`}>
          <h4 >{dentist.name}</h4>
        </Link>
        <p>{dentist.username}</p>

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

        <Link to= "/favs" >
          <button onClick={() => addFav(dentist)} className="favButton">‚≠ê</button>
        </Link>
    </div>
    ))}
    </>

  );
};

export default Card;
