import React, { useEffect, useState } from "react";
import Card from "../Components/Card";
import { useContextGlobal } from "../Components/utils/global.context";

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {

  const { dentists } = useContextGlobal();
  const [favorites, setFavorites] = useState([]);
  const favs = JSON.parse(localStorage.getItem("favs")) || {};
  console.log(favs)
  const favoriteDentists = Object.values(favs);
  console.log(favoriteDentists)

  useEffect(() => {
    const localStorageFavs = JSON.parse(localStorage.getItem('favs')) || [];
    setFavorites(localStorageFavs);
  }, []);

  return (
    <>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
      {favoriteDentists.map((item) => (
        <Card key={item.id} name={item.name} username={item.username} id={item.id} />
      ))}
    </div>
    </>
  );
};

export default Favs;
